mapper.insert:
INSERT INTO PARTY_TB(    PARTY_ID
					      , PARTY_TITLE
					      , P_DATE
					      , P_LOCATION
					      , P_LONG
					      , P_LATI
					      , P_NUM
					      , P_GENDER
					      , P_COST
					      , P_NOTE
					      , HASHTAG  )
              VALUES(    MP_BOARD_SEQ.NEXTVAL 
              		  , #{party_title}
              		  , #{p_date}
              		  , #{p_location}
              		  , #{p_long}
              		  , #{p_lati}
              		  , #{p_num}
              		  , #{p_gender}
              		  , #{p_cost}
              		  , #{p_note}
              		  , #{hashtag}	)

mapper.update:
UPDATE MP_BOARD
   SET TITLE    =   #{title},
   	   CONTENT  =   #{content}
 WHERE BNO = #{bno}

UPDATE MP_BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}
