mapper.insert:
 INSERT INTO USER_TB(   U_ID 
					       , NAME 
					       , EMAIL_ADDRESS
					       , PASSWORD
					       , AGE
					       , PHONE_NUMBER
					       , GENDER
					       , L_LEVEL)
               VALUES(    #{u_id} 
              		   , #{name}
              		   , #{email_address}
              		   , #{password}
              		   , #{age}
              		   , #{phone_number}
              		   , #{gender}
              		   , 0)

mapper.select:
SELECT U_ID, PASSWORD 
  FROM USER_TB
 WHERE U_ID = #{u_id}
   AND PASSWORD = #{password}

mapper.update:
UPDATE USER_TB SET
U_LEVEL = U_LEVEL + #{point}
WHERE U_ID = #{u_id}

UPDATE MP_MEMBER SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.delete:
DELETE FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
