mapper.select:
	  SELECT *
   	  FROM   PICKJOIN_TB
   	  WHERE  U_ID = #{u_id}
   	  AND    PARTY_ID = #{party_id}

mapper.update:
UPDATE PICKJOIN_TB
SET   PICK_FLAG = 0
WHERE U_ID = #{u_id}
AND   PARTY_ID = #{party_id}

UPDATE    PICKJOIN_TB
SET       JOIN_FLAG = 0
WHERE     U_ID = #{u_id}   
AND       PARTY_ID = #{party_id}
