mapper.insert:
 INSERT INTO USER_TB(   U_ID 
					       , NAME 
					       , EMAIL_ADDRESS
					       , PASSWORD
					       , AGE
					       , PHONE_NUMBER
					       , GENDER	)
               VALUES(    #{u_id} 
              		   , #{name}
              		   , #{email_address}
              		   , #{password}
              		   , #{age}
              		   , #{phone_number}
              		   , #{gender})

mapper.select:
SELECT USERID, USERPASS 
  FROM MP_MEMBER
 WHERE USERID = #{userId}
   AND USERPASS = #{userPass}

mapper.update:
UPDATE MP_MEMBER SET 
USERPASS = #{userPass},
USERNAME = #{userName}
WHERE USERID = #{userId}

mapper.delete:
DELETE FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
  AND USERPASS = #{userPass}
